import React from "react";

const showBtn = (i) => {
    document.getElementById('icon' + i).style.display = "flex";
}

const hideBtn = (i) => {
    document.getElementById('icon' + i).style.display = "none";
}

const Card = (props) => {
    return (
        <>
            <div className="mainCard" onMouseEnter={() => showBtn(props.key)} onMouseLeave={() => hideBtn(props.key)}>
                <div className="overlay">
                    <div className="buttons" id={"icon" + props.key}>
                        <a>
                            Save
                        </a>
                    </div>
                </div>
                <img src={props.image} ></img>
                <div className="detail">
                    <p>{props.name}</p>
                </div>
            </div>
            
        </>
    );
}
export default Card;
