import React from "react";
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './home.css';

import { useState, useRef } from "react";
const Home = () => {
    const carouselRef = useRef(null);
  const totalSlides = 2; // Update this with the total number of slides

  const [currentSlide, setCurrentSlide] = useState(0);

  const handleAfterSlide = (index) => {
    // When the last slide is reached, manually reset the carousel to the first slide
    if (index === totalSlides - 1) {
      setCurrentSlide(0);
      carouselRef.current.setCarouselState(0, 'final', true);
    } else {
      setCurrentSlide(index);
    }
  };
    return (
        <>
            <section className="home home-page">
                <div className="home-container">
                    <div className="row">
                        <div className="quotes-container">
                            <Carousel ref={carouselRef}
                showThumbs={false}
                showArrows={false}
                autoPlay={true}
                interval={3000}
                transitionTime={1000}
                infiniteLoop={true}
                swipeable={false}
                selectedItem={currentSlide}
                afterSlide={handleAfterSlide}>
                                <div className="quote">
                                    <h1>Home Decor <span>Made Easy</span></h1>
                                </div>
                                <div className="quote">
                                    <h1>Quote 2</h1>
                                </div>
                                <div className="quote">
                                    <h1>Quote 3</h1>
                                </div>
                            </Carousel>
                        </div>
                        <div className="images-container">
                            <div className="round">

                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}

export default Home;
