import React from "react";
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './home.css';
import { useState, useEffect } from "react";
const Home = () => {
    const quotes = [
        "Home Decor",
        "Made Easy",
        "Quote 2",
        "Quote 3"
      ];
    
      const [activeQuoteIndex, setActiveQuoteIndex] = useState(0);
    
      useEffect(() => {
        const interval = setInterval(() => {
          setActiveQuoteIndex((prevIndex) => (prevIndex + 1) % quotes.length);
        }, 5000); // Set the interval time for fading to 5 seconds (5000 milliseconds)
    
        return () => clearInterval(interval);
      }, []);
    return (
        <>
            <section className="home home-page">
                <div className="home-container">
                    <div className="row">
                        <div className="quotes-container">
                            <Carousel showThumbs={false} showArrows={false} autoPlay={true}
                                interval={3000} transitionTime={1000} infiniteLoop={true} swipeable={false}>
                                <div className="quote">
                                    <h1>Home Decor <span>Made Easy</span></h1>
                                </div>
                                <div className="quote">
                                    <h1>Quote 2</h1>
                                </div>
                                <div className="quote">
                                    <h1>Quote 3</h1>
                                </div>
                            </Carousel>
                        </div>
                        <div className="images-container">
                            <div className="round">

                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}

export default Home;
