import React, { useState } from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";

const Card = (props) => {
    const [isBookmarked, setIsBookmarked] = useState(false);

    useEffect(() => {
        const isProductBookmarked = props.localItems.some(
            (item) => item.Id === props.Id
        );
        setIsBookmarked(isProductBookmarked);
    },[props.localItems,props.Id]);
    
    const saveDesign = () =>{
        if(isBookmarked){
            props.unBookmark(props.Id);
        }
        else{
            props.bookmarked(props.Id);
        }
        setIsBookmarked((prev) => !prev);
    }
    return (
        <>

            <div className="mainCard" id={props.Id}>
                <Link to={"/detail/" + props.Id + "/" + props.name}>
                    <img src={props.image} ></img>
                </Link>
                <div className="buttons">
                    <a onClick={() => saveDesign(Id)} id="save-btn">{isBookmarked ? 'Saved' : 'Save'}</a>
                </div>
                <div className="detail">
                    <p>{props.name}</p>
                </div>
            </div>

        </>
    );
}
export default Card;
