import React from "react";
import { useParams } from "react-router-dom";
import products from "../designs";
import '../CSS/detail.css';
const Detail = (props) => {
    const {Id} = useParams();
    const {name} = useParams();
    let index = products.findIndex(x=>x.Id === Id)
    // let index2 = props.localItems.findIndex(x=>x.Id === Id);
    // if(index2){
    //     document.getElementById("save-btn").style.display = "none";
    //     document.getElementById("unsave-btn").style.display = "flex";
    // }
    // else{
    //     document.getElementById("save-btn").style.display = "flex";
    //     document.getElementById("unsave-btn").style.display = "none";
    // }
    const saveDesign = (Id) =>{
        document.getElementById("save-btn").style.display = "none";
        document.getElementById("unsave-btn").style.display = "flex";
        props.bookmarked(Id);
    }
    const unsaveDesign = (Id) =>{
        document.getElementById("save-btn").style.display = "flex";
        document.getElementById("unsave-btn").style.display = "none";
        props.unBookmark(Id);
    }
    return (
        <>
            <section className="detail-section">
                <div className="main-container">
                    <div className="productDetail">
                        <div className="col col-1">
                            <img src={"/" + products[index].image} ></img>
                        </div>
                        <div className="col col-2">
                            <div className="saveBtn">
                                <div className="buttons">
                                    <a onClick={()=>saveDesign(Id)} id="save-btn">Save</a>
                                    <a onClick={()=>unsaveDesign(Id)} id="unsave-btn">Saved</a>
                                </div>
                            </div>
                            <div className="heading">
                                <h2>{products[index].name}</h2>
                            </div>
                            <div className="para">
                                <p>{products[index].desp}</p>
                            </div>
                            <div className="mat-heading">
                                <h3>Material Required:</h3>
                            </div>
                            <div className="list">
                                <ul>
                                {products[index].material.map((m) => {
                                    return (
                                        <li>{m}</li>
                                    );
                                })}
                                </ul>
                            </div>
                            <div className="link-heading">
                                <h3>Link to video</h3>
                            </div>
                            <div className="link">
                                <a href={products[index].link} target="_blank" >{products[index].link}</a>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}
export default Detail;
